module.exports = ([string, pattern, newSubstring]) => {
  if (string == null || typeof string !== 'string') {
    return null;
  }
  if (pattern == null || newSubstring == null || typeof newSubstring !== 'string') {
    return string;
  }
  if (pattern instanceof RegExp) {
    return string.replace(pattern, newSubstring);
  }
  if (typeof pattern === 'string') {
    const regex = new RegExp(pattern);
    return string.replace(regex, newSubstring);
  }
  return string;
};
