module.exports = ([html, saveNewLines]) => {
  if (typeof html === 'string') {
    let htmlString = html;
    if (saveNewLines) {
      htmlString = htmlString.replace(/<(br)[\s\S]*\/>/ig, '\n');
    }
    htmlString = htmlString.replace(/(<([^>]+)>)/ig, '');
    return htmlString;
  }
  return null;
};
