const { extension: mimeExtension } = require('mime-types');

const extensionsFixNames = {
  markdown: 'md',
};

module.exports = ([param]) => {
  if (typeof param === 'string' && param !== '') {
    const extension = mimeExtension(param);
    const result = extensionsFixNames[extension]
      ? extensionsFixNames[extension]
      : extension;
    return result || null;
  }
  return null;
};
