const mimeType = require('./MIME_TYPE.runtime');

const saveWebImageMIME = [
  'image/apng', 'image/bmp', 'image/gif', 'image/x-icon', 'image/jpeg', 'image/png', 'image/svg+xml', 'image/tiff', 'image/webp',
];

module.exports = ([param]) => {
  if (typeof param === 'string' && param !== '') {
    const mime = mimeType([param]);
    const index = saveWebImageMIME.indexOf(mime);
    return (index !== -1);
  }
  return false;
};
