module.exports = ([value]) => {
  if (value == null) {
    return value;
  }
  if (parseInt(value, 10) === null || Number.isNaN(parseInt(value, 10))) {
    return null;
  }
  const result = Math.trunc(value);
  if (!Number.isFinite(result) || Number.isNaN(result)) {
    return null;
  }
  return result;
};
