module.exports = ([value]) => {
  if (typeof value === 'string') {
    return !value;
  }
  if (value == null) {
    // Null or undefined
    return true;
  }
  if (typeof value === 'boolean') {
    return false;
  }
  if (typeof value === 'number') {
    return Number.isNaN(value);
  }
  if ('length' in value) {
    return value.length === 0;
  }
  if (typeof value === 'object') {
    return Object.keys(value).length === 0;
  }
  return false;
};
