module.exports = ([num, base]) => {
  if (num == null || base == null) {
    return null;
  }
  if (typeof num === 'number'
      && Number.isFinite(num)
      && typeof base === 'number'
      && Number.isFinite(base)
  ) {
    if (base >= 2) {
      return num.toString(base);
    }
  }
  return null;
};
