function convertSchematicValueToObjectSchema(schema) {
  if (!schema || schema.type !== 'schematicValue') {
    return schema;
  }
  const metaSchema = schema.schema;
  return {
    type: 'object',
    properties: {
      value: metaSchema,
      schema: {
        type: 'schema',
        schema: metaSchema,
      },
    },
    required: ['value', 'schema'],
  };
}

module.exports.convertSchematicValueToObjectSchema = convertSchematicValueToObjectSchema;
