/**
 * Utilities for equality checks.
 * @module utils/equality
 */
const isEqualLodash = require('lodash.isequal');

/**
 * Performs a proper equality check whether the two given values
 * are considered equal. This is basically a wrapper around Lodash.isEqual
 * @param {*} value1 first value
 * @param {*} value2 second value
 * @returns {boolean} whether or not values are equal
 */
function isEqual(value1, value2) {
  return isEqualLodash(value1, value2);
}

/**
 * Returns whether or not the values are the same.
 * @param {*} value1 first value
 * @param {*} value2 second value
 * @returns {boolean} whether or not the values are the same
 */
function isSame(value1, value2) {
  return Object.is(value1, value2);
}

module.exports.isEqual = isEqual;
module.exports.isSame = isSame;
