function pickBy(obj, callback) {
  const newObj = {};
  if (obj != null && typeof obj === 'object') {
    Object.keys(obj).forEach((propName) => {
      const value = obj[propName];
      if (callback(propName, value)) {
        newObj[propName] = value;
      }
    });
  }
  return newObj;
}

module.exports.pickBy = pickBy;
