import { NullableSchema, Schema } from './schemas';

interface SchemaAssignmentOptions {
  references: Record<string, Schema>;
  subset?: boolean;
  tolerant?: boolean;
}

export interface ValidationError {
  errors: ValidationError[];
  message: string;
}

export function getSchemaAssignmentErrors(fromSchema: NullableSchema, toSchema: NullableSchema, options?: SchemaAssignmentOptions): ValidationError[];
export function isSchemaAssignable(fromSchema: NullableSchema, toSchema: NullableSchema, options?: SchemaAssignmentOptions): boolean;
export function isSubsetSchema(subSchema: NullableSchema, superSchema: NullableSchema, allowEquality?: boolean): boolean;
export function resolveMetaSchema(schema: NullableSchema): { errors: ValidationError[], schema: Schema | null };
