#!/usr/bin/env node
const AmendBTP = require('./amend-btp').Amender;
const amendArgs = require('./amend-args');
const AmendMobile = require('./amend-mobile').Amender;
const AmendSource = require('./amend-source').Amender;
const { AMEND_TARGET_BTP, AMEND_TARGET_MOBILE, AMEND_TARGET_SOURCE} = require('../utils');
function amend() {
  let target = amendArgs.target();
  let amender;
  if (target == AMEND_TARGET_BTP) {
    amender = new AmendBTP(amendArgs);
  } else if (target == AMEND_TARGET_MOBILE) {
    amender = new AmendMobile(amendArgs);
  } else if (target == AMEND_TARGET_SOURCE) {
    amender = new AmendSource(amendArgs);
  }
  return amender.amend();
};
module.exports = { amend };


