#!/usr/bin/env node
const CheckTS = require('./check-ts').Checker;
const checkDOC = require('./check-doc').Checker;
const checkBackend = require('./check-backend').Checker;
const checkArgs = require('./check-args');
const { FEATURE_TYPESCRIPT, FEATURE_DOCUMENT, FEATURE_BACKEND} = require('../utils');
function check() {
  let target = checkArgs.target();
  let checker;
  switch (target) {
    case FEATURE_TYPESCRIPT:
      checker = new CheckTS(checkArgs);
      break;
    case FEATURE_DOCUMENT:
      checker = new checkDOC(checkArgs);
      break;
    case FEATURE_BACKEND:
        checker = new checkBackend(checkArgs);
      break; 
  }
  
  return checker.check();
};
module.exports = { check };


