const chalk = require('chalk');

module.exports = {
	getInfo: function(msg) {
		return chalk.gray(msg);
	},

	getInfoBold: function(msg) {
		return chalk.bold(msg);
	},

	getInfoMore: function(msg) {
		// return chalk.white(msg);
		return msg;
	},

	getWarn: function(msg) {
		return chalk.yellow(msg);
	},

	getSuccess: function(msg) {
		return chalk.blue(msg);
	},

    getPrompt: function(msg) {
        return chalk.magenta(msg);
    },

	getError: function(msg) {
        return chalk.red(msg);
    },

	getPrefix: function (msg) {
		return chalk.cyan(msg);
	}
};